package cn.xu.model.dto.comment;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;import java.util.List;/** * 添加评论请求对象 * * 用于表示添加评论时的请求数据，包括评论的目标类型、目标ID、评论内容以及评论附带的图片链接。 */@Data@Schema(description = "添加评论请求对象")public class CommentAddRequest {    /**     * 评论类型     */    @Schema(description = "评论类型", required = true)    private Integer targetType;    /**     * 评论对象的ID     * 帖子ID，取决于评论类型     */    @Schema(description = "评论对象ID，帖子ID", required = true)    private Long targetId;    /**     * 评论内容     */    @Schema(description = "评论内容", required = true)    private String content;    /**     * 评论附带的图片URL列表     * 图片可以用来展示评论的图片内容     */    @Schema(description = "评论附带的图片URL列表", required = false)    private List<String> imageUrls;}