package cn.xu.model.dto.comment;import cn.xu.common.request.PageRequest;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;/** * 查询评论请求 * * 用于表示获取评论时的请求参数，包括目标ID、目标类型、排序方式等。 */@Data@Schema(description = "查询评论请求")public class FindCommentRequest extends PageRequest {    /**     * 目标ID     */    @Schema(description = "目标ID", required = true)    private Long targetId;    /**     * 目标类型     */    @Schema(description = "目标类型", required = true)    private Integer targetType;    /**     * 排序类型     * 如按时间、点赞数等方式排序     */    @Schema(description = "排序类型", required = false)    private String sortType;}