package cn.xu.model.dto.comment;import cn.xu.common.request.PageRequest;import lombok.Data;/** * 查询回复请求 * * 用于表示获取评论回复时的请求参数，包括父评论ID、排序方式等。 */@Datapublic class FindReplyRequest extends PageRequest {    /**     * 父评论ID     * 用于获取某个评论的回复列表     */    private Long parentId;    /**     * 排序类型     * 支持的排序类型：     * - HOT：按热度排序     * - TIME：按时间排序     */    private String sortType;}