package cn.xu.model.dto.favorite;import io.swagger.v3.oas.annotations.media.Schema;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;/** * 收藏统计响应DTO */@Data@Builder@NoArgsConstructor@AllArgsConstructor@Schema(description = "收藏统计响应DTO")public class FavoriteCountResponse {    /** 目标ID     */    @Schema(description = "目标ID")    private Long targetId;    /** 目标类型     */    @Schema(description = "目标类型")    private String targetType;    /** 收藏的数量     * - 表示当前目标被收藏的次数     */    @Schema(description = "收藏次数")    private Integer count;    /** 当前用户是否已经收藏     * - 如果用户已经收藏该目标，返回true，否则返回false     */    @Schema(description = "当前用户是否收藏")    private Boolean isFavorited;}