package cn.xu.model.dto.favorite;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;/** * 获取当前用户收藏列表请求DTO * 用于表示获取当前用户收藏列表的请求数据。 */@Data@Schema(description = "获取当前用户收藏列表请求DTO")public class GetMyFavoritesRequest {    /**     * 页码，默认为1     * 用于分页查询中当前页码的表示，默认值为1。     */    @Schema(description = "页码，从1开始，默认值为1", example = "1")    private Integer pageNo = 1;    /**     * 每页显示的条目数，默认为10     * 用于分页查询时每页展示的结果数量，最大值为100。     */    @Schema(description = "每页显示的条目数，默认值为10，最大值为100", example = "10")    private Integer pageSize = 10;    /**     * 收藏类型，用于筛选不同类型的收藏项     * 默认值为"POST"。     */    @Schema(description = "目标类型，默认为POST", example = "POST")    private String type = "POST";    /**     * 收藏的文件夹ID     * 用于指定某个文件夹中的收藏项，默认为null表示不指定文件夹。     */    @Schema(description = "收藏的文件夹ID，默认为null，表示不指定文件夹", example = "1")    private Long folderId;    /**     * 获取安全的页码，防止页码为负数或零     * @return 如果页码不合法（小于1），返回默认页码1     */    public int getSafePageNo() {        return pageNo != null && pageNo >= 1 ? pageNo : 1;    }    /**     * 获取安全的每页条目数，确保每页条目数在1到100之间     * @return 如果pageSize无效（小于1），返回默认10条；如果大于100，限制为100条     */    public int getSafePageSize() {        if (pageSize == null || pageSize < 1) {            return 10;        }        return Math.min(pageSize, 100);    }    /**     * 计算分页查询的偏移量（页码-1）* 每页条数     * @return 分页查询的偏移量     */    public int getOffset() {        return (getSafePageNo() - 1) * getSafePageSize();    }}