package cn.xu.model.dto.permission;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;/** * 角色添加或更新请求DTO * 用于添加或更新角色的请求数据。 */@Data@Schema(description = "角色添加或更新请求DTO")@JsonIgnoreProperties(ignoreUnknown = true)public class RoleAddOrUpdateRequest {    /**     * 角色ID     * 在更新时使用此字段，表示需要更新的角色ID；添加时此字段为空。     */    @Schema(description = "角色ID")    private Long id;    /**     * 角色代码     * 用于唯一标识角色的代码。     */    @Schema(description = "角色代码")    private String code;    /**     * 角色名称     * 角色的名称，用于展示和标识角色。     */    @Schema(description = "角色名称")    private String name;    /**     * 角色备注     * 用于对角色的描述或附加说明。     */    @Schema(description = "角色备注")    private String remark;}