package cn.xu.model.dto.post;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;import jakarta.validation.constraints.Size;import java.util.List;/** * 草稿请求参数 * 用于处理保存草稿请求的DTO，包含了草稿的标题、内容、描述、标签、封面等信息。 */@Data@JsonIgnoreProperties(ignoreUnknown = true)@Schema(description = "草稿请求")public class DraftRequest {    /**     * 草稿ID     * 用于更新已有草稿时提供草稿的唯一标识。     */    @Schema(description = "草稿ID")    private Long id;    /**     * 草稿标题     * 限制最大长度为100字符。     */    @Size(max = 100, message = "草稿标题长度不能超过100字符")    @Schema(description = "草稿标题")    private String title;    /**     * 草稿内容     * 限制最大长度为10000字符。     */    @Size(max = 10000, message = "草稿内容长度不能超过10000字符")    @Schema(description = "草稿内容")    private String content;    /**     * 草稿描述     * 提供草稿的简短描述。     */    @Schema(description = "草稿描述")    private String description;    /**     * 标签ID集合     * 用于给草稿添加标签，支持多选标签。     */    @Schema(description = "标签ID集合")    private List<Long> tagIds;    /**     * 封面URL     * 用于设置草稿的封面图片。     */    @Schema(description = "封面URL")    private String coverUrl;}