package cn.xu.model.dto.post;import cn.xu.common.request.PageRequest;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;/** * 帖子分页查询请求参数 * 用于分页查询帖子，并支持指定排序方式。 */@Data@JsonIgnoreProperties(ignoreUnknown = true)@Schema(description = "帖子分页查询请求参数")public class PostPageQueryRequest extends PageRequest {    /**     * 排序方式     * 用于指定查询结果的排序方式。默认为 "hottest"（按热度排序）。     */    @Schema(description = "排序方式")    private String sortBy = "hottest";}