package cn.xu.model.dto.post;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;/** * 帖子与标签关系DTO * 用于表示一个帖子与其关联的标签ID列表。 */@Data@NoArgsConstructor@AllArgsConstructorpublic class PostTagRelation {    /**     * 帖子ID     * 表示该帖子与标签之间的关联。     */    private Long postId;    /**     * 标签ID列表     * 表示与该帖子关联的标签IDs集合。     */    private List<Long> tagIds;}