package cn.xu.model.dto.post;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;/** * 标签统计信息 * 用于表示某个标签的统计数据，包括标签ID、标签名称以及与该标签相关联的帖子数量。 */@Data@NoArgsConstructor@AllArgsConstructorpublic class TagStatistics {    /**     * 标签ID     * 唯一标识标签的ID。     */    private Long tagId;    /**     * 标签名称     * 标签的名称。     */    private String tagName;    /**     * 帖子数量     * 与该标签相关联的帖子数量。     */    private Integer postCount;}