package cn.xu.model.dto.report;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;/** * 处理举报请求DTO * 用于处理举报的相关请求。 */@Data@Schema(description = "处理举报请求DTO")public class HandleReportRequestDTO {    /**     * 举报ID     * 用于指定需要处理的举报记录的ID。     */    @Schema(description = "举报ID")    @NotNull(message = "举报ID不能为空")    private Long reportId;    /**     * 处理结果     * 用于描述处理该举报的结果。     */    @Schema(description = "处理结果")    @NotBlank(message = "处理结果不能为空")    private String handleResult;    /**     * 是否处理     * 1-已处理，2-未处理     */    @Schema(description = "是否处理 1-已处理，2-未处理")    @NotNull(message = "处理状态不能为空")    private Integer status;}