package cn.xu.model.dto.report;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;/** * 举报请求DTO * 用于表示举报请求的输入数据。 */@Data@Schema(description = "举报请求DTO")public class ReportRequestDTO {    /**     * 举报类型     * 1-帖子 2-评论 3-用户 4-话题     */    @Schema(description = "举报类型 1-帖子 2-评论 3-用户 4-话题")    @NotNull(message = "举报类型不能为空")    private Integer targetType;    /**     * 被举报的目标ID     * 可以是帖子ID、评论ID、用户ID或话题ID，具体根据targetType来判断。     */    @Schema(description = "被举报的目标ID")    @NotNull(message = "被举报的目标ID不能为空")    private Long targetId;    /**     * 举报原因     * 用于描述举报的原因。     */    @Schema(description = "举报原因")    @NotBlank(message = "举报原因不能为空")    private String reason;    /**     * 举报详情     * 可选项，用于提供更详细的举报说明。     */    @Schema(description = "举报详情")    private String detail;}