package cn.xu.model.dto.user;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;import jakarta.validation.constraints.Email;import jakarta.validation.constraints.NotBlank;/** * 发送验证码请求 * * 用于请求发送验证码的详细信息。 */@Data@Schema(description = "发送验证码请求")public class SendVerifyCodeRequest {    /**     * 邮箱     */    @NotBlank(message = "邮箱不能为空")    @Email(message = "邮箱格式不正确")    @Schema(description = "目标邮箱", required = true, example = "user@example.com")    private String email;    /**     * 验证码场景     * register-注册, forgot-找回密码, bind-绑定邮箱     */    @Schema(description = "验证码场景：register-注册, forgot-找回密码, bind-绑定邮箱", example = "register")    private String scene;}