package cn.xu.model.dto.user;import io.swagger.v3.oas.annotations.media.Schema;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import javax.validation.constraints.Size;import java.io.Serializable;/** * 更新用户资料请求DTO * 提供给前端调用接口时传递的用户数据，遵循DDD规范 * * @author zhizhi */@Data@Builder@NoArgsConstructor@AllArgsConstructor@Schema(description = "更新用户资料请求")public class UpdateUserProfileRequest implements Serializable {    private static final long serialVersionUID = 1L;    @Schema(description = "昵称", example = "张三")    @Size(min = 2, max = 12, message = "昵称长度必须在2到12个字符之间")    private String nickname;    @Schema(description = "头像URL", example = "https://example.com/avatar.jpg")    private String avatar;    @Schema(description = "性别：1-男，2-女", example = "1")    private Integer gender;    @Schema(description = "个人简介", example = "我是一个热爱编程的工程师")    @Size(max = 200, message = "个人简介长度不能超过200个字符")    private String description;}