package cn.xu.model.dto.user;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Data;import javax.validation.constraints.Email;import javax.validation.constraints.NotBlank;import javax.validation.constraints.Pattern;import javax.validation.constraints.Size;/** * 用户注册请求DTO * * 注册流程： * 1. 必填项：email（邮箱） + password（密码）+ verifyCode（验证码） * 2. 可选：username（用户名，不填则自动生成） * 3. 可选：nickname（显示名，不填则使用username） */@Data@Schema(description = "用户注册请求参数")public class UserRegisterRequest {    /**     * 邮箱（必填）     * 用途：登录、找回密码、接收通知     */    @NotBlank(message = "邮箱不能为空")    @Email(message = "邮箱格式不正确")    @Schema(description = "邮箱（必填）", example = "zhangsan@example.com", required = true)    private String email;    /**     * 密码（必填）     */    @NotBlank(message = "密码不能为空")    @Size(min = 6, max = 20, message = "密码长度为6-20位")    @Schema(description = "密码（必填，6-20位）", example = "password123", required = true)    private String password;    /**     * 邮箱验证码（必填）     */    @NotBlank(message = "验证码不能为空")    @Size(min = 6, max = 6, message = "验证码为6位数字")    @Schema(description = "邮箱验证码（必填，6位数字）", example = "123456", required = true)    private String verifyCode;    /**     * 账号名（可选）     * 如果不填，系统自动生成唯一的username     * 格式：4-20位，允许字母、数字、下划线     */    @Pattern(regexp = "^[a-zA-Z0-9_]{4,20}$", message = "用户名必须是4-20位字母、数字或下划线")    @Schema(description = "账号名（可选，如果不填，系统自动生成）", example = "zhangsan123")    private String username;    /**     * 显示名（可选）     * 如果不填，默认使用username     * 用于前端显示，可以包含中文     */    @Size(max = 50, message = "昵称长度不能超过50字符")    @Schema(description = "显示名（可选，默认使用username）", example = "张三的技术博客")    private String nickname;}