package cn.xu.model.vo.user;import io.swagger.v3.oas.annotations.media.Schema;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;import java.time.LocalDateTime;/** * 用户点赞信息展示VO * 用于展示用户点赞信息及相关的点赞项目 * * @author zhizhi */@Data@Builder@NoArgsConstructor@AllArgsConstructor@Schema(description = "用户点赞信息展示VO")public class UserLikeItemVO implements Serializable {    private static final long serialVersionUID = 1L;    @Schema(description = "点赞ID")    private Long likeId;    @Schema(description = "目标项的ID，如文章ID、商品ID等")    private Long targetId;    @Schema(description = "目标项的标题")    private String targetTitle;    @Schema(description = "目标项的类型：1-文章，2-商品，3-评论等")    private Integer targetType;    @Schema(description = "目标项类型名称")    private String targetTypeName;    @Schema(description = "目标项链接")    private String targetUrl;    @Schema(description = "点赞时间")    private LocalDateTime likeTime;}