package cn.xu.model.vo.user;import io.swagger.v3.oas.annotations.media.Schema;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;/** * 用户个人资料视图对象 * 包含用户基础信息和统计信息 * 备注：DDD用于开发，正在进行系统测试，最终数据会更改为与生产环境相关的字段 * * @author zhizhi */@Data@NoArgsConstructor@AllArgsConstructor@Builder@Schema(description = "用户个人资料视图对象")public class UserProfileVO implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 用户基本信息     */    @Schema(description = "用户基本信息")    private UserBasicInfoVO basicInfo;    /**     * 统计数据     */    @Schema(description = "统计数据")    private UserProfileStatsVO stats;    /**     * 是否是当前登录用户的个人资料     */    @Schema(description = "是否是当前登录用户的个人资料")    private Boolean isOwnProfile;    /**     * 是否当前用户正在关注该用户     * 说明：表示当前用户是否关注了该目标用户     */    @Schema(description = "是否当前用户正在关注该用户，说明：表示当前用户是否关注了该目标用户")    private Boolean isFollowing;    /**     * 是否被目标用户关注     * 说明：表示目标用户是否关注了当前用户     */    @Schema(description = "是否被目标用户关注，说明：表示目标用户是否关注了当前用户")    private Boolean isFollowedBy;    /**     * 用户基本信息视图对象     */    @Data    @NoArgsConstructor    @AllArgsConstructor    @Builder    @Schema(description = "用户基本信息")    public static class UserBasicInfoVO implements Serializable {        private static final long serialVersionUID = 1L;        @Schema(description = "用户ID")        private Long id;        @Schema(description = "用户名")        private String username;        @Schema(description = "昵称")        private String nickname;        @Schema(description = "头像")        private String avatar;        @Schema(description = "性别：1-男，2-女")        private Integer gender;        @Schema(description = "个人简介")        private String description;        @Schema(description = "联系电话")        private String phone;        @Schema(description = "邮箱")        private String email;        @Schema(description = "所在地区")        private String region;        @Schema(description = "生日")        private String birthday;        @Schema(description = "创建时间")        private java.time.LocalDateTime createTime;    }    /**     * 用户统计数据视图对象     */    @Data    @NoArgsConstructor    @AllArgsConstructor    @Builder    @Schema(description = "用户个人资料统计数据")    public static class UserProfileStatsVO implements Serializable {        private static final long serialVersionUID = 1L;        @Schema(description = "帖子数量")        private Long postCount;        @Schema(description = "关注数量")        private Long followCount;        @Schema(description = "粉丝数量")        private Long fansCount;        @Schema(description = "点赞数量")        private Long likeCount;        @Schema(description = "评论数量")        private Long commentCount;        @Schema(description = "收藏数量")        private Long collectionCount;    }}