mapper.select:
SELECT id, user_id, name, description, is_default, content_count, is_public, sort, create_time, update_time
FROM collect_folder
WHERE id = #{id}

mapper.update:
UPDATE collect_folder
SET name = #{name}, description = #{description}, is_default = #{isDefault}, content_count = #{contentCount}, 
    is_public = #{isPublic}, sort = #{sort}, update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, name, description, is_default, content_count, is_public, sort, create_time, update_time
FROM collect_folder
WHERE user_id = #{userId} AND name = #{name}

mapper.update:
UPDATE collect_folder
SET content_count = #{count}
WHERE id = #{folderId}
