mapper.select:
SELECT id, user_id, target_id, folder_id, target_type, status, create_time, update_time
FROM collect
WHERE user_id = #{userId} AND target_id = #{targetId} AND target_type = #{targetType}

mapper.update:
UPDATE collect
SET folder_id = #{folderId}
WHERE id = #{id}

