mapper.update:
UPDATE `column`
name = #{name}, description = #{description}, cover_url = #{coverUrl}, status = #{status}, sort = #{sort}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT

FROM `column`


        AND status = #{status}


        AND is_recommended = #{isRecommended}


        AND user_id = #{userId}


ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM `column`


        AND status = #{status}


        AND is_recommended = #{isRecommended}


        AND user_id = #{userId}

mapper.update:
UPDATE `column`
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE `column`
SET is_recommended = #{isRecommended}, update_time = NOW()
WHERE id = #{id}
