mapper.update:
UPDATE comment
SET like_count = like_count + #{count}
WHERE id = #{commentId}

UPDATE comment
SET reply_count = reply_count + #{count}
WHERE id = #{commentId}

mapper.select:
SELECT c.*
FROM comment c
WHERE c.target_type = #{targetType}
  AND c.target_id = #{targetId}
  AND (c.parent_id IS NULL OR c.parent_id = 0)
ORDER BY hot_score DESC
    LIMIT #{offset}, #{pageSize}

SELECT
c.*,
0 AS hot_score 
FROM comment c
WHERE
c.target_type = #{targetType}
AND c.target_id = #{targetId}
AND (c.parent_id IS NULL OR c.parent_id = 0)
ORDER BY c.create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT * FROM ( SELECT c.*, 0 AS hot_score, ROW_NUMBER() OVER ( PARTITION BY c.parent_id ORDER BY c.create_time DESC ) AS rn FROM comment c WHERE c.parent_id IN
#{parentId}
) AS ranked WHERE rn <= #{previewSize}

SELECT

FROM comment
WHERE target_type = #{targetType}
AND target_id = #{targetId}
ORDER BY create_time DESC

mapper.delete:
DELETE FROM comment
WHERE id IN

    #{id}

DELETE
FROM comment
WHERE target_type = #{targetType}
  AND target_id = #{targetId}

mapper.select:
SELECT

