AND parent_id IS NULL

    AND target_type = #{targetType}

ORDER BY create_time DESC

SELECT

FROM comment
WHERE parent_id = #{parentId}
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT

FROM comment
WHERE parent_id IS NULL

    AND target_type = #{targetType}


    AND target_id = #{targetId}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT c.*, 0 AS hot_score FROM comment c WHERE c.parent_id IN
#{parentId}
ORDER BY c.hot_score DESC, c.create_time DESC LIMIT #{size}

SELECT c.*, 0 AS hot_score FROM comment c WHERE c.parent_id IN
#{parentId}
ORDER BY c.create_time DESC LIMIT #{size}

SELECT
FROM comment WHERE id IN
#{id}

SELECT COUNT(*)
FROM comment
WHERE target_type = #{targetType} AND target_id = #{targetId}

SELECT target_id as targetId, COUNT(*) as count FROM comment WHERE target_type = #{targetType} AND target_id IN
#{targetId}
GROUP BY target_id
