mapper.update:
UPDATE `conversation`
SET last_message_time = #{lastMessageTime},
    update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT COUNT(1) > 0
FROM `conversation`
WHERE user_id_1 = #{userId1} AND user_id_2 = #{userId2}

SELECT 
FROM `conversation`
WHERE user_id_1 = #{userId} OR user_id_2 = #{userId}
ORDER BY last_message_time DESC
LIMIT #{offset}, #{limit}
