mapper.delete:
DELETE FROM favorite
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND target_type = #{targetType}

mapper.select:
SELECT target_id
FROM favorite
WHERE user_id = #{userId}
AND target_type = #{targetType}
AND status = 1
ORDER BY create_time DESC

SELECT target_id
FROM favorite
WHERE user_id = #{userId}
AND target_type = #{targetType}
AND status = 1
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM favorite
WHERE user_id = #{userId}
AND target_type = #{targetType}
AND status = 1

SELECT COUNT(*)
FROM favorite
WHERE target_id = #{targetId}
AND target_type = #{targetType}
AND status = 1

SELECT user_id
FROM favorite
WHERE target_id = #{targetId}
AND target_type = #{targetType}
AND status = 1

SELECT target_id
FROM favorite
WHERE user_id = #{userId}
AND target_type = #{targetType}
AND folder_id = #{folderId}
AND status = 1
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM favorite
WHERE user_id = #{userId}
AND target_type = #{targetType}
AND folder_id = #{folderId}
AND status = 1

mapper.update:
UPDATE favorite
SET folder_id = #{newFolderId}, update_time = NOW()
WHERE id = #{id} AND user_id = #{userId}
