mapper.insert:
INSERT INTO file_record (
    storage_name, original_name, storage_path, bucket_name, size_bytes, mime_type, extension,
    upload_user_id, status, create_time, update_time
) VALUES (
    #{fileName}, #{originalFileName}, #{storagePath}, #{bucketName}, #{fileSize}, #{contentType}, #{extension},
    #{uploadUserId}, #{status}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT 
FROM file_record
WHERE upload_user_id = #{userId}

    AND status = #{status}

ORDER BY create_time DESC

mapper.update:
UPDATE file_record
storage_name = #{fileName}, original_name = #{originalFileName}, storage_path = #{storagePath}, size_bytes = #{fileSize}, mime_type = #{contentType}, extension = #{extension}, upload_user_id = #{uploadUserId}, bucket_name = #{bucketName}, status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE file_record
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM file_record
WHERE upload_user_id = #{userId}

    AND status = #{status}
