mapper.update:
UPDATE `follow`
SET follower_id = #{followerId},
    followed_id = #{followedId},
    status = #{status},
    update_time = #{updateTime}
WHERE id = #{id}

UPDATE `follow`
SET status = #{status}
WHERE follower_id = #{followerId}
  AND followed_id = #{followedId}

mapper.select:
SELECT

FROM `follow`
WHERE follower_id = #{followerId} AND followed_id = #{followedId}

SELECT status
FROM `follow`
WHERE follower_id = #{followerId}
  AND followed_id = #{followedId}
