mapper.update:
UPDATE `like`
SET status = #{status}
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}

mapper.delete:
DELETE FROM `like`
WHERE target_id = #{targetId}
AND type = #{type}

mapper.select:
SELECT

FROM `like`
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}
LIMIT 1

SELECT status
FROM `like`
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}
LIMIT 1

SELECT COUNT(*)
FROM `like`
WHERE target_id = #{targetId}
AND type = #{type}
AND status = 1
