mapper.update:
update message
type = #{type}, sender_id = #{senderId}, receiver_id = #{receiverId}, title = #{title}, content = #{content}, target_id = #{targetId}, is_read = #{isRead}, status = #{status}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select

from message
where receiver_id = #{userId}

    and type = #{type}

order by create_time desc
limit #{offset}, #{limit}

SELECT 
FROM message
WHERE type = 2
AND receiver_id = #{receiverId}
AND sender_id = #{senderId}
AND status = #{status}
ORDER BY create_time ASC
LIMIT #{offset}, #{limit}

SELECT 
FROM message
WHERE type = 2
AND sender_id = #{senderId}
AND receiver_id = #{receiverId}
ORDER BY create_time ASC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE message
SET status = #{newStatus},
    update_time = NOW()
WHERE type = 2
AND sender_id = #{senderId}
AND receiver_id = #{receiverId}
AND status = #{oldStatus}
