mapper.update:
UPDATE notification
type = #{type}, receiver_id = #{receiverId}, sender_id = #{senderId}, title = #{title}, content = #{content}, business_type = #{businessType}, business_id = #{businessId}, status = #{status}, is_read = #{isRead}, read_time = #{readTime}, update_time = now()
WHERE id = #{id}

mapper.select:
SELECT

FROM notification
WHERE receiver_id = #{receiverId}
AND status = 1

    AND type = #{type}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT

FROM notification
WHERE receiver_id = #{receiverId}
AND status = 1

    AND type = #{type}

ORDER BY create_time DESC
LIMIT #{pageable.offset}, #{pageable.pageSize}

SELECT

FROM notification
WHERE receiver_id = #{receiverId}
AND status = 1

    AND type = #{type}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

mapper.delete:
DELETE FROM notification
WHERE id IN

    #{id}
