mapper.update:
UPDATE post
SET view_count = #{viewCount}
WHERE id = #{postId}

UPDATE post
SET like_count = like_count + #{count}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count + #{count}
WHERE id = #{postId}

UPDATE post
SET collect_count = collect_count + #{count}
WHERE id = #{postId}

mapper.select:
SELECT 

FROM post
WHERE id = #{id}

SELECT COUNT(1) > 0
FROM post
WHERE id = #{id}

SELECT 

FROM post
WHERE status = 1
  AND category_id = #{categoryId}
ORDER BY create_time DESC
LIMIT #{offset}, #{size}

SELECT
FROM post WHERE status = 1 AND user_id IN
#{userId}
ORDER BY create_time DESC LIMIT #{offset}, #{limit}

SELECT
FROM post WHERE status = 1 AND user_id IN
#{userId}
ORDER BY create_time DESC LIMIT #{offset}, #{limit}

SELECT 

FROM post
WHERE status = 1
  AND type = #{type}
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT 

FROM post
WHERE status = 1
  AND type = #{type}
  AND id != #{excludePostId}
ORDER BY create_time DESC
LIMIT #{limit}
