mapper.select:
SELECT tag_id
FROM post_tag
WHERE post_id = #{postId}

SELECT post_id
FROM post_tag
WHERE tag_id = #{tagId}
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT
FROM post_tag WHERE post_id IN
#{postId}

mapper.delete:
DELETE FROM post_tag
WHERE post_id = #{postId} AND tag_id IN

    #{tagId}

mapper.select:
SELECT COUNT(*)
FROM post_tag
WHERE tag_id = #{tagId}
