mapper.select:
SELECT topic_id
FROM post_topic
WHERE post_id = #{postId}

SELECT post_id
FROM post_topic
WHERE topic_id = #{topicId}
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT
FROM post_topic WHERE post_id IN
#{postId}

mapper.delete:
DELETE FROM post_topic
WHERE post_id = #{postId} AND topic_id IN

    #{topicId}
