mapper.select:
SELECT 
FROM user_conversation
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}

mapper.update:
UPDATE user_conversation
SET unread_count = 0, update_time = NOW()
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}

UPDATE user_conversation
SET unread_count = unread_count + 1, update_time = NOW()
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}

UPDATE user_conversation
SET is_deleted = 1, update_time = NOW()
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}

UPDATE user_conversation
SET other_nickname = #{nickname}, other_avatar = #{avatar}, update_time = NOW()
WHERE other_user_id = #{userId}

UPDATE user_conversation
SET is_blocked = #{isBlocked}, update_time = NOW()
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}

UPDATE user_conversation
SET is_blocked_by = #{isBlockedBy}, update_time = NOW()
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}

UPDATE user_conversation
SET relation_type = #{relationType}, update_time = NOW()
WHERE owner_id = #{ownerId} AND other_user_id = #{otherUserId}
