mapper.update:
UPDATE user
username = #{username}, password = #{password}, email = #{email}, nickname = #{nickname}, avatar = #{avatar}, gender = #{gender}, phone = #{phone}, region = #{region}, birthday = #{birthday}, description = #{description}, status = #{status}, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE user
SET follow_count = follow_count + #{count}
WHERE id = #{followerId}

UPDATE user
SET fans_count = fans_count + #{count}
WHERE id = #{followeeId}

mapper.select:
SELECT COUNT(*)
FROM user
WHERE username = #{username}

SELECT COUNT(*)
FROM user
WHERE email = #{email}

SELECT
FROM user WHERE id IN
#{id}

SELECT
FROM user WHERE id IN
#{userId}

SELECT id, username, password
FROM user
WHERE username = #{username}
