mapper.select:
SELECT a.id, a.title, a.description, a.cover_url AS coverUrl, a.create_time AS createTime, a.update_time AS updateTime, c.name AS categoryName, GROUP_CONCAT(t.name SEPARATOR ',') AS tagNames, u.nickname FROM article AS a JOIN article_category_relation AS ac ON a.id = ac.article_id JOIN article_category AS c ON ac.category_id = c.id LEFT JOIN article_tag_relation AS at ON a.id = at.article_id LEFT JOIN article_tag AS t ON at.tag_id = t.id LEFT JOIN user AS u ON a.user_id = u.id
AND a.title LIKE CONCAT('%', #{title}, '%')
AND ac.category_id = #{categoryId}
AND EXISTS (SELECT 1 FROM article_tag_relation WHERE article_id = a.id AND tag_id = #{tagId})
GROUP BY a.id, c.name, u.nickname ORDER BY a.create_time DESC LIMIT #{pageNo}, #{pageSize}

SELECT a.id, a.title, a.cover_url, a.description, a.user_id, u.nickname, c.name as category_name, GROUP_CONCAT(DISTINCT t.name ORDER BY t.name SEPARATOR ',') as tag_names, a.create_time, a.update_time FROM article a LEFT JOIN user u ON a.user_id = u.id LEFT JOIN article_category_relation ac ON a.id = ac.article_id LEFT JOIN article_category c ON ac.category_id = c.id LEFT JOIN article_tag_relation at ON a.id = at.article_id LEFT JOIN article_tag t ON at.tag_id = t.id
AND a.title LIKE CONCAT('%', #{title}, '%')
AND ac.category_id = #{categoryId}
AND at.tag_id = #{tagId}
GROUP BY a.id, a.title, a.cover_url, a.description, a.user_id, u.nickname, c.name, a.create_time, a.update_time ORDER BY a.create_time DESC LIMIT #{offset}, #{size}
