mapper.delete:
DELETE FROM `like`
WHERE user_id = #{userId}
  AND target_id = #{targetId}
  AND type = #{type}

DELETE FROM `like`
WHERE user_id = #{userId}
  AND target_id = #{targetId}
  AND type = #{type}

mapper.select:
SELECT COUNT(*)
FROM `like`
WHERE target_id = #{targetId}
  AND type = #{type}
  AND status = 1

SELECT COUNT(*) > 0
FROM `like`
WHERE user_id = #{userId}
  AND target_id = #{targetId}
  AND type = #{type}
  AND status = 1

mapper.update:
UPDATE `like`
SET count = #{count}
WHERE target_id = #{targetId}
  AND type = #{type}

