mapper.update:
UPDATE `like`

    status = #{status},

WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}

mapper.select:
SELECT

FROM `like`
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}
LIMIT 1

SELECT status
FROM `like`
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}
LIMIT 1
