mapper.insert:
INSERT INTO `like` (
    user_id, target_id, type, status, create_time, update_time
) VALUES (
    #{userId}, #{targetId}, #{type}, #{status}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE `like`

    status = #{status},
    update_time = #{updateTime}

WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}

mapper.select:
SELECT 
FROM `like`
WHERE user_id = #{userId}
AND target_id = #{targetId}
AND type = #{type}
LIMIT 1

SELECT COUNT(1)
FROM `like`
WHERE target_id = #{targetId}
AND type = #{type}
AND status = 1

SELECT user_id
FROM `like`
WHERE target_id = #{targetId}
AND type = #{type}
AND status = 1

SELECT 
FROM `like`
WHERE type = #{type}
AND status = 1
ORDER BY id ASC
LIMIT #{offset}, #{limit}
